import React, { useState } from 'react';
import { motion } from 'framer-motion';
import BeforeAfterSlider from './BeforeAfterSlider';

const services = [
  {
    id: 'background-magic',
    title: 'Background Magic',
    icon: 'ðŸŒ…',
    description: 'Transport your photos to any location with our AI background transformation.',
    features: [
      'Any background you can imagine',
      'High-resolution output',
      'Natural-looking results',
      'Quick delivery'
    ],
    beforeImage: '/images/beach-before.jpg',
    afterImage: '/images/beach-after.jpg',
    beforeAlt: 'Original scene',
    afterAlt: 'Transformed scene'
  },
  {
    id: 'fantasy-characters',
    title: 'Fantasy Characters',
    icon: 'ðŸ‘‘',
    description: 'Turn yourself into superheroes, fantasy characters, or anything you imagine.',
    features: [
      'Wide range of character styles',
      'Customizable details',
      'High-quality transformation',
      'Fast processing'
    ],
    beforeImage: '/images/character-before.jpg',
    afterImage: '/images/character-after.jpg',
    beforeAlt: 'Original character',
    afterAlt: 'Transformed character'
  },
  {
    id: '3d-characters',
    title: '3D Printed Characters',
    icon: 'ðŸŽ¨',
    description: 'Get your transformed characters as beautiful Low Poly 3D printed figures.',
    features: [
      'Professional 3D modeling',
      'High-quality printing',
      'Customizable poses',
      'Quick shipping'
    ],
    singleImage: '/images/3d-print.jpg',
    singleImageAlt: '3D Printed Character'
  }
];

const ServiceOptionsModal: React.FC<{
  isOpen: boolean;
  onClose: () => void;
  service: typeof services[0] | null;
}> = ({ isOpen, onClose, service }) => {
  if (!isOpen || !service) return null;

  return (
    <div className="modal-overlay" style={{ display: isOpen ? 'flex' : 'none' }}>
      <div className="modal-content">
        <button className="modal-close" onClick={onClose}>
          <i className="fas fa-times"></i>
        </button>
        <h3 id="modal-service-title">{service.title}</h3>
        <p id="modal-intro-text">Wybierz format, w jakim chcesz otrzymaÄ‡ swoje magiczne zdjÄ™cie:</p>
        
        <div className="modal-options-container">
          <div className="product-option">
            <i className="fas fa-download" id="option1-icon"></i>
            <h4 id="option1-title">Wersja Cyfrowa</h4>
            <p id="option1-description">Otrzymaj swoje magiczne zdjÄ™cie w wysokiej rozdzielczoÅ›ci w formacie cyfrowym.</p>
            <p className="price" id="option1-price">29 zÅ‚</p>
            <button className="btn-primary" id="option1-cta">Wybieram CyfrowÄ…</button>
          </div>
          
          <div className="product-option">
            <i className="fas fa-frame" id="option2-icon"></i>
            <h4 id="option2-title">Wersja Ramowana</h4>
            <p id="option2-description">PiÄ™knie wydrukowane i oprawione w eleganckÄ… ramÄ™.</p>
            <p className="price" id="option2-price">89 zÅ‚</p>
            <button className="btn-primary" id="option2-cta">Wybieram RamowanÄ…</button>
          </div>
        </div>
      </div>
    </div>
  );
};

const Services: React.FC = () => {
  const [selectedService, setSelectedService] = useState<typeof services[0] | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleServiceClick = (service: typeof services[0]) => {
    setSelectedService(service);
    setIsModalOpen(true);
  };

  return (
    <div className="services-section">
      <div className="services-container">
        <div className="services-header">
          <h2>Our Services</h2>
          <p>Transform your photos into magical moments</p>
        </div>

        <div className="services-grid">
          {services.map((service) => (
            <motion.div
              key={service.id}
              className="service-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <div className="service-title">
                <div className="service-icon">{service.icon}</div>
                <h3>{service.title}</h3>
              </div>
              <p className="service-description">{service.description}</p>
              
              {service.beforeImage && service.afterImage ? (
                <div className="service-image">
                  <BeforeAfterSlider
                    beforeImage={service.beforeImage}
                    afterImage={service.afterImage}
                    beforeAlt={service.beforeAlt}
                    afterAlt={service.afterAlt}
                  />
                </div>
              ) : service.singleImage ? (
                <div className="service-image">
                  <img 
                    src={service.singleImage} 
                    alt={service.singleImageAlt}
                    className="single-image"
                  />
                </div>
              ) : null}
              
              <div className="best-photos">
                <h4>Best for:</h4>
                <ul>
                  {service.features.map((feature, index) => (
                    <li key={index}>
                      <span className="check-icon">âœ“</span>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>

              <button
                className="order-button"
                onClick={() => handleServiceClick(service)}
                data-service={service.id}
              >
                Get Started
              </button>
            </motion.div>
          ))}
        </div>
      </div>

      <ServiceOptionsModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        service={selectedService}
      />
    </div>
  );
};

export default Services;
