import React from 'react';
import { useCart } from '../context/CartContext';

interface CartProps {
  isOpen: boolean;
  onClose: () => void;
}

const Cart: React.FC<CartProps> = ({ isOpen, onClose }) => {
  const { cartItems, removeFromCart } = useCart();

  const generateCartMessage = () => {
    const itemsList = cartItems
      .map(item => `- ${item.name} (ID: ${item.id})`)
      .join('\n');
    
    return `Hi! I'm interested in the following 3D prints from your website:\n${itemsList}\nCould you please provide more details or ordering information?`;
  };

  const handleWhatsApp = () => {
    const message = generateCartMessage();
    const encodedMessage = encodeURIComponent(message);
    window.open(`https://wa.me/YOUR_PHONE_NUMBER?text=${encodedMessage}`, '_blank');
  };

  const handleMessenger = () => {
    const message = generateCartMessage();
    navigator.clipboard.writeText(message);
    alert('Message copied to clipboard! Please paste it in your Messenger chat.');
    window.open('https://m.me/YOUR_PAGE_USERNAME', '_blank');
  };

  const handleEmail = () => {
    const message = generateCartMessage();
    const encodedMessage = encodeURIComponent(message);
    const subject = encodeURIComponent('Inquiry about 3D Prints');
    window.open(`mailto:petetrainerlsf@gmail.com?subject=${subject}&body=${encodedMessage}`);
  };

  if (!isOpen) return null;

  return (
    <div className="cart-modal-overlay" onClick={onClose}>
      <div className="cart-modal-content" onClick={e => e.stopPropagation()}>
        <button className="cart-modal-close" onClick={onClose}>×</button>
        <h2>My 3D Print Wishlist</h2>
        
        <div id="cart-items-container">
          {cartItems.length === 0 ? (
            <p>Your wishlist is empty</p>
          ) : (
            cartItems.map(item => (
              <div key={item.id} className="cart-item">
                <div className="cart-item-thumbnail">
                  <img 
                    src={item.mainImage || '/images/placeholder.jpg'} 
                    alt={item.name} 
                    onError={(e) => {
                      console.error(`Failed to load image for ${item.name}:`, item.mainImage);
                      e.currentTarget.src = '/images/placeholder.jpg';
                    }}
                  />
                </div>
                <div className="cart-item-details">
                  <span className="cart-item-name">{item.name}</span>
                  {item.quantity && item.quantity > 1 && (
                    <span className="cart-item-quantity">Quantity: {item.quantity}</span>
                  )}
                </div>
                <button 
                  className="remove-item-btn"
                  onClick={() => removeFromCart(item.id)}
                >
                  ×
                </button>
              </div>
            ))
          )}
        </div>

        <div className="cart-actions">
          <button onClick={handleWhatsApp} className="checkout-btn whatsapp">
            <i className="fab fa-whatsapp"></i> WhatsApp
          </button>
          <button onClick={handleMessenger} className="checkout-btn messenger">
            <i className="fab fa-facebook-messenger"></i> Messenger
          </button>
          <button onClick={handleEmail} className="checkout-btn email">
            <i className="fas fa-envelope"></i> Email
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
