import React from 'react';
import { motion } from 'framer-motion';

interface PrintItem {
  id: string;
  name: string;
  description: string;
  price: string;
  image: string;
}

const printItems: PrintItem[] = [
  {
    id: 'robot-toy',
    name: 'Cute Flower',
    description: 'A charming low-poly flower perfect for display or play',
    price: '$9.99',
    image: '/images/flower1.png'
  },
  {
    id: 'fantasy-creature',
    name: 'Mystical Snake Warrior',
    description: 'An intricately detailed snake figure with dynamic pose',
    price: '$39.99',
    image: '/images/humanSnake.jpg'
  },
  {
    id: 'character-model',
    name: 'Fantasy Warrior',
    description: 'A detailed warrior character model with armor and weapons',
    price: '$34.99',
    image: '/images/3d-print.jpg'
  }
];

const ThreeDPrints: React.FC = () => {
  const handleOrderClick = (item: PrintItem) => {
    // Get the modal element
    const modal = document.getElementById('service-options-modal');
    if (!modal) return;

    // Create the modal content
    const modalContent = `
      <div class="modal-content">
        <button class="modal-close">&times;</button>
        <h2>Order 3D Print</h2>
        <div class="modal-confirmation">
          <div class="selection-summary">
            <h3>${item.name}</h3>
            <p>Price: <strong>${item.price}</strong></p>
            <img src="${item.image}" alt="${item.name}" style="max-width: 200px; margin: 1rem 0;">
            <p>${item.description}</p>
          </div>
          <p class="contact-instructions">To finalize your order, please contact us using your preferred method below:</p>
          <div class="contact-buttons">
            <a href="https://wa.me/48512233222?text=Hi!%20I'd%20like%20to%20order%20the%203D%20Print:%20'${encodeURIComponent(item.name)}'%20for%20${encodeURIComponent(item.price)}" 
               target="_blank" 
               rel="noopener noreferrer" 
               class="contact-button whatsapp">
              <i className="fab fa-whatsapp"></i>
              WhatsApp
            </a>
            <a href="https://m.me/aiphotomagic" 
               target="_blank" 
               rel="noopener noreferrer" 
               class="contact-button messenger">
              <i className="fab fa-facebook-messenger"></i>
              Messenger
            </a>
            <a href="mailto:contact@aiphotomagic.com?subject=3D%20Print%20Order:%20${encodeURIComponent(item.name)}&body=I'd%20like%20to%20order%20the%20${encodeURIComponent(item.name)}%20for%20${encodeURIComponent(item.price)}" 
               class="contact-button email">
              <i className="fas fa-envelope"></i>
              Email
            </a>
          </div>
        </div>
      </div>
    `;

    // Set the modal content
    modal.innerHTML = modalContent;

    // Add event listeners
    const closeButton = modal.querySelector('.modal-close');
    if (closeButton) {
      closeButton.addEventListener('click', () => {
        modal.style.display = 'none';
      });
    }

    // Show the modal
    modal.style.display = 'flex';
  };

  return (
    <div className="prints-section">
      <div className="prints-container">
        <motion.div
          className="prints-header"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h2>3D Prints Collection</h2>
          <p>Browse our curated collection of unique 3D printed toys and figures, ready to be made just for you!</p>
        </motion.div>

        <div className="prints-grid">
          {printItems.map((item) => (
            <motion.div
              key={item.id}
              className="print-item-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              whileHover={{ scale: 1.02 }}
            >
              <div className="print-item-image">
                <img src={item.image} alt={item.name} />
              </div>
              <div className="print-item-info">
                <h4 className="print-item-title">{item.name}</h4>
                <p className="print-item-description">{item.description}</p>
                <p className="print-item-price">{item.price}</p>
                <button
                  className="order-print-button"
                  onClick={() => handleOrderClick(item)}
                  data-model-name={item.name}
                  data-model-price={item.price}
                  data-model-image={item.image}
                >
                  Order This Print
                </button>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ThreeDPrints;
