import React, { useState, useEffect, useRef, useCallback } from 'react';

interface BeforeAfterSliderProps {
  beforeImage: string;
  afterImage: string;
  beforeAlt?: string;
  afterAlt?: string;
}

const BeforeAfterSlider: React.FC<BeforeAfterSliderProps> = ({
  beforeImage,
  afterImage,
  beforeAlt = "Before image",
  afterAlt = "After image"
}) => {
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState(50);
  const [beforeImageError, setBeforeImageError] = useState(false);
  const [afterImageError, setAfterImageError] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    console.log('Loading images:', { beforeImage, afterImage });
  }, [beforeImage, afterImage]);

  const updatePosition = useCallback((clientX: number) => {
    if (!containerRef.current) return;
    
    const rect = containerRef.current.getBoundingClientRect();
    const x = Math.max(0, Math.min(clientX - rect.left, rect.width));
    const percentage = (x / rect.width) * 100;
    setPosition(percentage);
  }, []);

  const handleMove = useCallback((clientX: number) => {
    if (isDragging) {
      updatePosition(clientX);
    }
  }, [isDragging, updatePosition]);

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);
    updatePosition(e.clientX);
  }, [updatePosition]);

  const handleTouchStart = useCallback((e: React.TouchEvent) => {
    e.preventDefault();
    setIsDragging(true);
    updatePosition(e.touches[0].clientX);
  }, [updatePosition]);

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      handleMove(e.clientX);
    };

    const handleTouchMove = (e: TouchEvent) => {
      e.preventDefault();
      handleMove(e.touches[0].clientX);
    };

    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove, { passive: false });
      document.addEventListener('mouseup', handleMouseUp);
      document.addEventListener('touchmove', handleTouchMove, { passive: false });
      document.addEventListener('touchend', handleMouseUp);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
      document.removeEventListener('touchmove', handleTouchMove);
      document.removeEventListener('touchend', handleMouseUp);
    };
  }, [isDragging, handleMove, handleMouseUp]);

  const handleBeforeImageError = () => {
    console.error('Error loading before image:', beforeImage);
    setBeforeImageError(true);
  };

  const handleAfterImageError = () => {
    console.error('Error loading after image:', afterImage);
    setAfterImageError(true);
  };

  return (
    <div 
      ref={containerRef}
      className="before-after-container"
      onMouseDown={handleMouseDown}
      onTouchStart={handleTouchStart}
    >
      <div 
        className="before-image"
        style={{ clipPath: `polygon(0 0, ${position}% 0, ${position}% 100%, 0 100%)` }}
      >
        <img 
          src={beforeImage} 
          alt={beforeAlt} 
          onError={handleBeforeImageError}
        />
        {beforeImageError && (
          <div className="image-error">
            Error loading before image
          </div>
        )}
      </div>
      <div className="after-image">
        <img 
          src={afterImage} 
          alt={afterAlt} 
          onError={handleAfterImageError}
        />
        {afterImageError && (
          <div className="image-error">
            Error loading after image
          </div>
        )}
      </div>
      <div 
        className="slider-handle"
        style={{ left: `${position}%` }}
      />
    </div>
  );
};

export default BeforeAfterSlider;
