import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import BeforeAfterSlider from './BeforeAfterSlider';
import { Link } from 'react-router-dom';

// Contact information constants
const WHATSAPP_NUMBER = '48512233222';
const MESSENGER_USERNAME = 'aiphotomagic';
const EMAIL_ADDRESS = 'contact@aiphotomagic.com';

const services = [
  {
    id: 'background-magic',
    title: 'Background Magic',
    icon: 'fas fa-magic',
    description: 'Transform your photos with stunning AI-generated backgrounds. Perfect for portraits, product photos, and creative projects.',
    image: '/images/beach-after.jpg',
    features: [
      'Professional quality backgrounds',
      'Multiple style options',
      'Quick turnaround',
      'High-resolution output',
      'Perfect for social media'
    ]
  },
  {
    id: 'fantasy-characters',
    title: 'Fantasy Characters',
    icon: 'fas fa-dragon',
    description: 'Turn yourself into a fantasy character with our AI-powered transformation service. Become a wizard, elf, or any magical being you can imagine.',
    image: '/images/character-after.jpg',
    features: [
      'Multiple fantasy themes',
      'High-quality transformations',
      'Customizable options',
      'Fast delivery',
      'Perfect for cosplay'
    ]
  },
  {
    id: '3d-characters',
    title: 'On-Demand 3D Toy Printing',
    icon: 'fas fa-cube',
    description: "Have a link to a great 3D model of a toy, figure, or character you'd love to hold? We offer professional 3D printing services to bring your chosen models to life!",
    image: '/images/3d-print.jpg',
    features: [
      'High-quality 3D printing (FDM technology)',
      'Printing with safe PLA material in various colors',
      'We print models you provide (link required, subject to review)',
      'Careful finishing and support removal',
      'Get a personalized quote based on the model'
    ],
    directContact: true
  }
];

interface SelectedOption {
  name: string;
  price: string;
  type: 'digital' | 'framed';
}

const ServiceOptionsModal: React.FC<{
  isOpen: boolean;
  onClose: () => void;
  service: typeof services[0] | null;
}> = ({ isOpen, onClose, service }) => {
  const [selectedOption, setSelectedOption] = useState<SelectedOption | null>(null);

  useEffect(() => {
    if (!isOpen) {
      setSelectedOption(null);
    }
  }, [isOpen]);

  const handleOptionSelect = (option: SelectedOption) => {
    setSelectedOption(option);
  };

  const generateWhatsAppLink = (service: string, option: SelectedOption) => {
    const message = encodeURIComponent(
      `Hi! I'd like to order the '${option.name}' ('${service}') for ${option.price}.`
    );
    return `https://wa.me/${WHATSAPP_NUMBER}?text=${message}`;
  };

  const generateEmailLink = (service: string, option: SelectedOption) => {
    const subject = encodeURIComponent(`Order Request: ${service} - ${option.name}`);
    const body = encodeURIComponent(
      `Hi! I'd like to order the '${option.name}' ('${service}') for ${option.price}.\n\nMy details are...`
    );
    return `mailto:${EMAIL_ADDRESS}?subject=${subject}&body=${body}`;
  };

  if (!isOpen || !service) return null;

  return (
    <div className="modal-overlay" style={{ display: isOpen ? 'flex' : 'none' }}>
      <div className="modal-content">
        <button className="modal-close" onClick={onClose}>
          <i className="fas fa-times"></i>
        </button>
        <h3 id="modal-service-title">{service.title}</h3>
        
        {!selectedOption ? (
          <>
            <p id="modal-intro-text">Choose how you'd like to receive your magical transformation:</p>
            <div className="modal-options-container">
              <div className="product-option">
                <i className="fas fa-desktop"></i>
                <h4>Digital Version</h4>
                <p>High-resolution digital file delivered via email</p>
                <div className="price">$29</div>
                <button 
                  className="modal-button primary add-to-cart-button"
                  data-service-name="Background Magic"
                  data-variant-name="Digital Version"
                  data-price="29"
                  data-image-url="/images/background-magic-example.jpg"
                >
                  Select Digital
                </button>
              </div>
              
              <div className="product-option">
                <i className="fas fa-image"></i>
                <h4>Framed Print</h4>
                <p>Premium quality print with elegant frame</p>
                <div className="price">$99</div>
                <button 
                  className="modal-button primary add-to-cart-button"
                  data-service-name="Background Magic"
                  data-variant-name="Framed Print"
                  data-price="99"
                  data-image-url="/images/background-magic-example.jpg"
                >
                  Select Framed
                </button>
              </div>
            </div>
          </>
        ) : (
          <div className="modal-confirmation">
            <p id="modal-intro-text">Great choice! To finalize your order:</p>
            <p className="selection-summary">
              You selected: <strong>{selectedOption.name}</strong> for <strong>{service.title}</strong> ({selectedOption.price})
            </p>
            <p className="contact-instructions">
              Please contact us using your preferred method below to finalize your order and send your photo. Remember to mention your chosen option!
            </p>
            <div className="modal-contact-buttons">
              <a 
                href={generateWhatsAppLink(service.title, selectedOption)}
                className="contact-button whatsapp"
                target="_blank"
                rel="noopener noreferrer"
              >
                <i className="fab fa-whatsapp"></i>
                Contact via WhatsApp
              </a>
              <a 
                href={`https://m.me/${MESSENGER_USERNAME}`}
                className="contact-button messenger"
                target="_blank"
                rel="noopener noreferrer"
              >
                <i className="fab fa-facebook-messenger"></i>
                Contact via Messenger
              </a>
              <a 
                href={generateEmailLink(service.title, selectedOption)}
                className="contact-button email"
              >
                <i className="fas fa-envelope"></i>
                Contact via Email
              </a>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const Services: React.FC = () => {
  const [selectedService, setSelectedService] = useState<typeof services[0] | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleServiceClick = (service: typeof services[0]) => {
    setSelectedService(service);
    setIsModalOpen(true);
  };

  return (
    <div className="services-section">
      <div className="services-container">
        <div className="services-header">
          <h2>Our Services</h2>
          <p>Transform your photos into magical moments</p>
        </div>

        <div className="services-grid">
          {services.map((service, index) => (
            <div key={index} className="service-card">
              <div className="service-title">
                <i className={`service-icon ${service.icon}`}></i>
                <h3>{service.title}</h3>
              </div>
              <p className="service-description">{service.description}</p>
              <div className="service-image">
                {service.id === 'background-magic' ? (
                  <BeforeAfterSlider
                    beforeImage="/images/beach-before.jpg"
                    afterImage="/images/beach-after.jpg"
                    beforeAlt="Original scene"
                    afterAlt="Transformed scene"
                  />
                ) : service.id === 'fantasy-characters' ? (
                  <BeforeAfterSlider
                    beforeImage="/images/character-before.jpg"
                    afterImage="/images/character-after.jpg"
                    beforeAlt="Original character"
                    afterAlt="Transformed character"
                  />
                ) : (
                  <img src={service.image} alt={service.title} />
                )}
              </div>
              <div className="best-photos">
                <h4>Service Highlights:</h4>
                <ul>
                  {service.features.map((feature, idx) => (
                    <li key={idx}>
                      <i className="fas fa-check check-icon"></i>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>
              {service.directContact ? (
                <Link to="/contact" className="order-button">
                  Get Printing Quote
                </Link>
              ) : (
                <button className="order-button" onClick={() => handleServiceClick(service)}>
                  Get Started
                </button>
              )}
            </div>
          ))}
        </div>
      </div>

      <ServiceOptionsModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        service={selectedService}
      />
    </div>
  );
};

export default Services;
