import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useCart } from '../context/CartContext';
import './ThreeDPrints.css';

interface PrintItem {
  id: string;
  title: string;
  description: string;
  price: string;
  mainImage: string;
  images: string[];
  details: string[];
}

const printItems: PrintItem[] = [
  {
    id: 'print1',
    title: 'Robot Sculpture',
    description: 'A detailed 3D printed robot sculpture with intricate mechanical details.',
    price: '$49.99',
    mainImage: '/images/humanSnake.png',
    images: [
      '/images/humanSnake.png',
      '/images/humanSnake.png',
      '/images/humanSnake.png'
    ],
    details: [
      'Height: 20cm',
      'Material: PLA',
      'Print Time: 12 hours',
      'Weight: 300g'
    ]
  },
  {
    id: 'print2',
    title: 'Geometric Flower',
    description: 'A beautiful geometric flower design with interlocking petals.',
    price: '$39.99',
    mainImage: '/images/flower1.png',
    images: [
      '/images/flower1.png',
      '/images/flower1.png',
      '/images/flower1.png'
    ],
    details: [
      'Diameter: 15cm',
      'Material: PLA',
      'Print Time: 8 hours',
      'Weight: 200g'
    ]
  },
  {
    id: 'print3',
    title: 'Warrior Statue',
    description: 'An impressive warrior statue with detailed armor and weaponry.',
    price: '$59.99',
    mainImage: '/images/warrior.png',
    images: [
      '/images/warrior.png',
      '/images/warrior.png',
      '/images/warrior.png'
    ],
    details: [
      'Height: 25cm',
      'Material: PLA',
      'Print Time: 15 hours',
      'Weight: 400g'
    ]
  }
];

const ThreeDPrints: React.FC = () => {
  const [selectedPrint, setSelectedPrint] = useState<PrintItem | null>(null);
  const [activeImage, setActiveImage] = useState<string | null>(null);
  const { addToCart, cartItems, removeFromCart } = useCart();
  const [itemQuantities, setItemQuantities] = useState<Record<string, number>>(() => {
    // Initialize quantities from cart items
    const initialQuantities: Record<string, number> = {};
    cartItems.forEach(item => {
      if (item.quantity !== undefined) {
        initialQuantities[item.id] = item.quantity;
      }
    });
    return initialQuantities;
  });

  // Update quantities when cart items change
  useEffect(() => {
    const newQuantities: Record<string, number> = {};
    cartItems.forEach(item => {
      if (item.quantity !== undefined) {
        newQuantities[item.id] = item.quantity;
      }
    });
    setItemQuantities(newQuantities);
  }, [cartItems]);

  const openModal = (print: PrintItem) => {
    setSelectedPrint(print);
    setActiveImage(print.mainImage);
  };

  const closeModal = () => {
    setSelectedPrint(null);
    setActiveImage(null);
  };

  const handleThumbnailClick = (image: string) => {
    setActiveImage(image);
  };

  const handleAddToCart = (print: PrintItem) => {
    const quantity = itemQuantities[print.id] || 1;
    addToCart({
      id: print.id,
      name: print.title,
      mainImage: print.mainImage,
      quantity: quantity,
      price: print.price
    });
  };

  const handleQuantityChange = (printId: string, newQuantity: number) => {
    const validQuantity = Math.max(1, Math.min(99, newQuantity));
    setItemQuantities(prev => ({
      ...prev,
      [printId]: validQuantity
    }));

    // If item is in cart, update cart quantity
    const cartItem = cartItems.find(item => item.id === printId);
    if (cartItem) {
      addToCart({
        ...cartItem,
        quantity: validQuantity
      });
    }
  };

  const isItemInCart = (printId: string) => {
    return cartItems.some(item => item.id === printId);
  };

  const handleRemoveFromCart = (e: React.MouseEvent, printId: string) => {
    e.stopPropagation();
    removeFromCart(printId);
  };

  const calculateTotalPrice = (price: string, quantity: number) => {
    const numericPrice = parseFloat(price.replace('$', ''));
    return `$${(numericPrice * quantity).toFixed(2)}`;
  };

  return (
    <div className="prints-section">
      <div className="prints-container">
        <motion.div
          className="prints-header"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h2>3D Prints Collection</h2>
          <p>Browse our curated collection of unique 3D printed toys and figures, ready to be made just for you!</p>
        </motion.div>

        <div className="prints-grid">
          {printItems.map((print) => (
            <motion.div
              key={print.id}
              className="print-item-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              whileHover={{ scale: 1.02 }}
              onClick={() => openModal(print)}
            >
              <div className="print-item-image">
                <img 
                  src={print.mainImage} 
                  alt={print.title}
                  onError={(e) => {
                    console.error(`Failed to load image for ${print.title}:`, print.mainImage);
                    e.currentTarget.src = '/images/placeholder.jpg';
                  }}
                />
              </div>
              <div className="print-item-info">
                <h4 className="print-item-title">{print.title}</h4>
                <p className="print-item-description">{print.description}</p>
                <div className="print-item-price-container">
                  <div className="print-item-quantity-controls">
                    <div className={`quantity-controls ${isItemInCart(print.id) ? 'disabled' : ''}`}>
                      <button 
                        className="quantity-btn"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleQuantityChange(print.id, (itemQuantities[print.id] || 1) - 1);
                        }}
                        disabled={isItemInCart(print.id)}
                      >
                        -
                      </button>
                      <input
                        type="number"
                        min="1"
                        max="99"
                        value={itemQuantities[print.id] || 1}
                        onChange={(e) => {
                          e.stopPropagation();
                          handleQuantityChange(print.id, parseInt(e.target.value) || 1);
                        }}
                        onClick={(e) => e.stopPropagation()}
                        disabled={isItemInCart(print.id)}
                      />
                      <button 
                        className="quantity-btn"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleQuantityChange(print.id, (itemQuantities[print.id] || 1) + 1);
                        }}
                        disabled={isItemInCart(print.id)}
                      >
                        +
                      </button>
                    </div>
                  </div>
                  <span className="print-item-price">{print.price}</span>
                </div>
                <button
                  className={`add-to-cart-btn ${isItemInCart(print.id) ? 'in-cart' : ''}`}
                  onClick={(e) => {
                    e.stopPropagation();
                    if (isItemInCart(print.id)) {
                      handleRemoveFromCart(e, print.id);
                    } else {
                      handleAddToCart(print);
                    }
                  }}
                >
                  <i className={`fas ${isItemInCart(print.id) ? 'fa-check' : 'fa-cart-plus'}`}></i>
                  {isItemInCart(print.id) ? 'In Cart' : 'Add to Cart'}
                  {isItemInCart(print.id) && (
                    <span 
                      className="remove-from-cart"
                      onClick={(e) => handleRemoveFromCart(e, print.id)}
                    >
                      <i className="fas fa-times"></i>
                    </span>
                  )}
                </button>
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Modal */}
      {selectedPrint && (
        <div className="print-modal-overlay" onClick={closeModal}>
          <div className="print-modal-content" onClick={e => e.stopPropagation()}>
            <button className="print-modal-close" onClick={closeModal}>
              <i className="fas fa-times"></i>
            </button>
            
            <div className="print-modal-gallery">
              <div className="print-modal-main-image">
                <img 
                  src={activeImage || selectedPrint.mainImage} 
                  alt={selectedPrint.title}
                />
              </div>
              <div className="print-modal-thumbnails">
                {[selectedPrint.mainImage, ...selectedPrint.images].map((image, index) => (
                  <div
                    key={index}
                    className={`print-modal-thumbnail ${activeImage === image ? 'active' : ''}`}
                    onClick={() => setActiveImage(image)}
                  >
                    <img src={image} alt={`${selectedPrint.title} - View ${index + 1}`} />
                  </div>
                ))}
              </div>
            </div>

            {/* Right Column - Product Info */}
            <div className="print-modal-info">
              <h2 className="print-modal-title">{selectedPrint.title}</h2>
              <p className="print-modal-description">{selectedPrint.description}</p>
              
              <div className="print-modal-details">
                <h4>Product Details</h4>
                <ul className="print-details-list">
                  {selectedPrint.details.map((detail, index) => (
                    <li key={index}>{detail}</li>
                  ))}
                </ul>
              </div>
              
              <div className="print-modal-quantity">
                <label htmlFor="quantity">Quantity:</label>
                <div className={`quantity-controls ${isItemInCart(selectedPrint.id) ? 'disabled' : ''}`}>
                  <button 
                    className="quantity-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleQuantityChange(selectedPrint.id, (itemQuantities[selectedPrint.id] || 1) - 1);
                    }}
                    disabled={isItemInCart(selectedPrint.id)}
                  >
                    -
                  </button>
                  <input
                    type="number"
                    id="quantity"
                    min="1"
                    max="99"
                    value={itemQuantities[selectedPrint.id] || 1}
                    onChange={(e) => {
                      e.stopPropagation();
                      handleQuantityChange(selectedPrint.id, parseInt(e.target.value) || 1);
                    }}
                    disabled={isItemInCart(selectedPrint.id)}
                  />
                  <button 
                    className="quantity-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleQuantityChange(selectedPrint.id, (itemQuantities[selectedPrint.id] || 1) + 1);
                    }}
                    disabled={isItemInCart(selectedPrint.id)}
                  >
                    +
                  </button>
                </div>
              </div>

              <div className="print-modal-price">
                <span className="unit-price">Unit Price: {selectedPrint.price}</span>
                <span className="total-price">
                  Total: {calculateTotalPrice(selectedPrint.price, itemQuantities[selectedPrint.id] || 1)}
                </span>
              </div>
              
              <button
                className={`print-modal-order-btn ${isItemInCart(selectedPrint.id) ? 'in-cart' : ''}`}
                onClick={(e) => {
                  e.stopPropagation();
                  if (selectedPrint) {
                    if (isItemInCart(selectedPrint.id)) {
                      // Don't remove from cart when clicking the main button
                      // Just do nothing when it's already in cart
                    } else {
                      handleAddToCart(selectedPrint);
                    }
                  }
                }}
              >
                <i className={`fas ${isItemInCart(selectedPrint.id) ? 'fa-check' : 'fa-cart-plus'}`}></i>
                {isItemInCart(selectedPrint.id) ? 'In Cart' : 'Add to Cart'}
                {isItemInCart(selectedPrint.id) && (
                  <span 
                    className="remove-from-cart"
                    onClick={(e) => {
                      e.stopPropagation();
                      e.preventDefault();
                      removeFromCart(selectedPrint.id);
                    }}
                  >
                    <i className="fas fa-times"></i>
                  </span>
                )}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ThreeDPrints;
