import React from 'react';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';

interface CartProps {
  isOpen: boolean;
  onClose: () => void;
}

const Cart: React.FC<CartProps> = ({ isOpen, onClose }) => {
  const { cartItems, removeFromCart, updateQuantity, getTotalPrice } = useCart();

  const generateCartMessage = () => {
    const itemsList = cartItems
      .map(item => {
        const quantity = item.quantity || 1;
        return `- ${item.name} (${quantity}x) at ${item.price} each`;
      })
      .join('\n');
    
    const total = getTotalPrice();
    return `Hi! I'm interested in the following 3D prints from your website:\n${itemsList}\n\nTotal: $${total}\nCould you please provide more details or ordering information?`;
  };

  const handleWhatsApp = () => {
    const message = generateCartMessage();
    const encodedMessage = encodeURIComponent(message);
    window.open(`https://wa.me/YOUR_PHONE_NUMBER?text=${encodedMessage}`, '_blank');
  };

  const handleMessenger = () => {
    const message = generateCartMessage();
    navigator.clipboard.writeText(message);
    alert('Message copied to clipboard! Please paste it in your Messenger chat.');
    window.open('https://m.me/YOUR_PAGE_USERNAME', '_blank');
  };

  const handleEmail = () => {
    const message = generateCartMessage();
    const encodedMessage = encodeURIComponent(message);
    const subject = encodeURIComponent('Inquiry about 3D Prints');
    window.open(`mailto:petetrainerlsf@gmail.com?subject=${subject}&body=${encodedMessage}`);
  };

  const handleQuantityChange = (itemId: string, newQuantity: number) => {
    updateQuantity(itemId, Math.max(1, Math.min(99, newQuantity)));
  };

  if (!isOpen) return null;

  return (
    <div className="cart-modal-overlay" onClick={onClose}>
      <div className="cart-modal-content" onClick={e => e.stopPropagation()}>
        <button className="cart-modal-close" onClick={onClose}>Ã—</button>
        <h2>My 3D Print Wishlist</h2>
        
        <div id="cart-items-container">
          {cartItems.length === 0 ? (
            <div className="empty-cart-message">
              <p>Your wishlist is empty</p>
              <p className="empty-cart-suggestion">
                âœ¨ Discover our amazing creations! Check out our{' '}
                <Link to="/services" className="cart-section-link" onClick={onClose}>
                  magical Services
                </Link>
                {' '}or browse through unique{' '}
                <Link to="/prints" className="cart-section-link" onClick={onClose}>
                  3D Prints
                </Link>
                {' '}to find something special just for you! ðŸŽ¨
              </p>
            </div>
          ) : (
            <>
              {cartItems.map(item => {
                const quantity = item.quantity || 1;
                
                return (
                  <div key={item.id} className="cart-item">
                    <div className="cart-item-thumbnail">
                      {item.id.includes('fantasy-characters') || item.id.includes('background-magic') ? (
                        <div className="cart-service-icon">
                          <i className={`fas ${
                            item.id.includes('fantasy-characters') ? 'fa-wand-magic-sparkles' : 'fa-image'
                          }`}></i>
                        </div>
                      ) : (
                        <img 
                          src={item.mainImage || '/images/placeholder.jpg'} 
                          alt={item.name} 
                          onError={(e) => {
                            console.error(`Failed to load image for ${item.name}:`, item.mainImage);
                            e.currentTarget.src = '/images/placeholder.jpg';
                          }}
                        />
                      )}
                    </div>
                    <div className="cart-item-details">
                      <span className="cart-item-name">{item.name}</span>
                      <div className="cart-item-quantity-controls">
                        <div className="quantity-controls">
                          <button 
                            className="quantity-btn"
                            onClick={() => handleQuantityChange(item.id, quantity - 1)}
                          >
                            -
                          </button>
                          <input
                            type="number"
                            min="1"
                            max="99"
                            value={quantity}
                            onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value) || 1)}
                          />
                          <button 
                            className="quantity-btn"
                            onClick={() => handleQuantityChange(item.id, quantity + 1)}
                          >
                            +
                          </button>
                        </div>
                      </div>
                      <div className="cart-item-price">
                        <span className="item-price">{item.price} each</span>
                      </div>
                    </div>
                    <button 
                      className="remove-item-btn"
                      onClick={() => removeFromCart(item.id)}
                    >
                      Ã—
                    </button>
                  </div>
                );
              })}
              <div className="cart-total">
                <span>Total:</span>
                <span className="total-amount">${getTotalPrice()}</span>
              </div>
            </>
          )}
        </div>

        <div className="cart-actions">
          {cartItems.length > 0 && (
            <p id="cart-contact-instructions">
              <strong>Final Step: Contact Us!</strong> Please use one of the buttons below to send us this summary via your preferred channel. <em>This is an inquiry, not a final purchase.</em> We will personally contact you to confirm your order, discuss shipping for framed items, and arrange payment.
            </p>
          )}
          <button className="checkout-btn whatsapp" onClick={handleWhatsApp}>
            <i className="fab fa-whatsapp"></i> WhatsApp
          </button>
          <button className="checkout-btn messenger" onClick={handleMessenger}>
            <i className="fab fa-facebook-messenger"></i> Messenger
          </button>
          <button className="checkout-btn email" onClick={handleEmail}>
            <i className="fas fa-envelope"></i> Email
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
