import React, { useState, useEffect } from 'react';

interface CartItem {
  id: string;
  name: string;
  price: number;
  image: string;
}

interface ShoppingCartProps {
  isOpen: boolean;
  onClose: () => void;
}

const ShoppingCart: React.FC<ShoppingCartProps> = ({ isOpen, onClose }) => {
  const [cartItems, setCartItems] = useState<CartItem[]>([]);

  useEffect(() => {
    // Load cart items from localStorage on component mount
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCartItems(JSON.parse(savedCart));
    }
  }, []);

  const removeFromCart = (productId: string) => {
    const updatedCart = cartItems.filter(item => item.id !== productId);
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const clearCart = () => {
    setCartItems([]);
    localStorage.removeItem('cart');
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  const generateCartMessage = () => {
    return cartItems.map(item => 
      `- ${item.name} (${item.price.toFixed(2)} zł)`
    ).join('\n');
  };

  const handleWhatsAppClick = () => {
    const message = `Hi! I'd like to inquire about the following items:\n\n${generateCartMessage()}`;
    const encodedMessage = encodeURIComponent(message);
    window.open(`https://wa.me/48512233222?text=${encodedMessage}`, '_blank');
  };

  const handleMessengerClick = () => {
    const message = `Hi! I'd like to inquire about the following items:\n\n${generateCartMessage()}`;
    const encodedMessage = encodeURIComponent(message);
    window.open(`https://m.me/aiphotomagic?ref=${encodedMessage}`, '_blank');
  };

  const handleEmailClick = () => {
    const subject = 'Inquiry about AI Photo Magic products';
    const body = `Hi,\n\nI'd like to inquire about the following items:\n\n${generateCartMessage()}`;
    window.location.href = `mailto:contact@aiphotomagic.com?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
  };

  return (
    <div className={`cart-modal ${isOpen ? 'open' : ''}`}>
      <div className="cart-modal-header">
        <h2 className="cart-modal-title">Your Cart</h2>
        <button className="cart-modal-close" onClick={onClose}>&times;</button>
      </div>

      <div className="cart-items-container">
        {cartItems.length === 0 ? (
          <p style={{ color: '#a0aec0', textAlign: 'center', padding: '2rem' }}>
            Your cart is empty
          </p>
        ) : (
          cartItems.map(item => (
            <div key={item.id} className="cart-item">
              <div className="cart-item-image">
                <img src={item.image} alt={item.name} />
              </div>
              <div className="cart-item-info">
                <div className="cart-item-name">{item.name}</div>
                <div className="cart-item-price">{item.price.toFixed(2)} zł</div>
              </div>
              <button
                className="cart-item-remove"
                onClick={() => removeFromCart(item.id)}
              >
                ×
              </button>
            </div>
          ))
        )}
      </div>

      <div className="cart-modal-footer">
        <div className="cart-total">
          <span>Total:</span>
          <span className="cart-total-price">{calculateTotal().toFixed(2)} zł</span>
        </div>

        <div className="cart-actions">
          <button className="cart-clear" onClick={clearCart}>
            Clear Cart
          </button>

          <div className="cart-send-inquiry">
            Send your inquiry via:
          </div>

          <div className="cart-inquiry-buttons">
            <a
              href="#"
              className="cart-inquiry-button whatsapp"
              onClick={handleWhatsAppClick}
            >
              <i className="fab fa-whatsapp"></i>
              WhatsApp
            </a>
            <a
              href="#"
              className="cart-inquiry-button messenger"
              onClick={handleMessengerClick}
            >
              <i className="fab fa-facebook-messenger"></i>
              Messenger
            </a>
            <a
              href="#"
              className="cart-inquiry-button email"
              onClick={handleEmailClick}
            >
              <i className="fas fa-envelope"></i>
              Email
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShoppingCart;
