import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useCart } from '../context/CartContext';

interface PrintItem {
  id: string;
  name: string;
  description: string;
  price: string;
  mainImage: string;
  images: string[];
  dimensions: string;
  material: string;
  productionTime: string;
  video?: string;
  quantity?: number;
}

const printItems: PrintItem[] = [
  {
    id: 'print1',
    name: 'Cute Robot Toy',
    description: 'A charming and detailed robot figure with articulated joints. Perfect for collectors and robot enthusiasts.',
    price: '$29.99',
    mainImage: '/images/prints/humanSnake.png',
    images: [
      '/images/prints/humanSnake.png',
      '/images/prints/humanSnake.png',
      '/images/prints/humanSnake.png',
    ],
    dimensions: 'H: 15cm, W: 8cm, D: 6cm',
    material: 'Eco-friendly PLA',
    productionTime: '3-5 business days',
    video: '/videos/robot-360.mp4'
  },
  {
    id: 'print2',
    name: 'Flower Vase',
    description: 'Elegant 3D printed vase with intricate floral patterns. A beautiful addition to any home decor.',
    price: '$24.99',
    mainImage: '/images/prints/flower1.png',
    images: [
      '/images/prints/flower1.png',
      '/images/prints/flower1.png',
      '/images/prints/flower1.png',
    ],
    dimensions: 'H: 20cm, Diameter: 12cm',
    material: 'Premium PLA',
    productionTime: '2-3 business days',
    video: '/videos/robot-360.mp4'
  },
  {
    id: 'print3',
    name: 'Warrior Figure',
    description: 'Detailed warrior figure with dynamic pose and intricate armor details. A must-have for fantasy collectors.',
    price: '$34.99',
    mainImage: '/images/prints/warrior.png',
    images: [
      '/images/prints/warrior.png',
      '/images/prints/warrior.png',
      '/images/prints/warrior.png',
    ],
    dimensions: 'H: 18cm, W: 10cm, D: 8cm',
    material: 'Premium PLA',
    productionTime: '4-6 business days',
    video: '/videos/robot-360.mp4'
  }
];

const ThreeDPrints: React.FC = () => {
  const [selectedPrint, setSelectedPrint] = useState<PrintItem | null>(null);
  const [activeImage, setActiveImage] = useState<string>('');
  const { addToCart } = useCart();

  const openModal = (print: PrintItem) => {
    setSelectedPrint(print);
    setActiveImage(print.mainImage);
  };

  const closeModal = () => {
    setSelectedPrint(null);
    setActiveImage('');
  };

  const handleThumbnailClick = (image: string) => {
    setActiveImage(image);
  };

  const handleAddToCart = (print: PrintItem) => {
    addToCart({
      id: print.id,
      name: print.name,
      mainImage: print.mainImage,
      quantity: print.quantity || 1
    });
  };

  const handleQuantityChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (selectedPrint) {
      const newQuantity = parseInt(e.target.value) || 1;
      setSelectedPrint({
        ...selectedPrint,
        quantity: Math.max(1, Math.min(99, newQuantity))
      });
    }
  };

  return (
    <div className="prints-section">
      <div className="prints-container">
        <motion.div
          className="prints-header"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h2>3D Prints Collection</h2>
          <p>Browse our curated collection of unique 3D printed toys and figures, ready to be made just for you!</p>
        </motion.div>

        <div className="prints-grid">
          {printItems.map((print) => (
            <motion.div
              key={print.id}
              className="print-item-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              whileHover={{ scale: 1.02 }}
              onClick={() => openModal(print)}
            >
              <div className="print-item-image">
                <img src={print.mainImage} alt={print.name} />
              </div>
              <div className="print-item-info">
                <h4 className="print-item-title">{print.name}</h4>
                <p className="print-item-description">{print.description}</p>
                <p className="print-item-price">{print.price}</p>
                <button
                  className="add-to-cart-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleAddToCart(print);
                  }}
                >
                  <i className="fas fa-cart-plus"></i>
                  Add to Wishlist
                </button>
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Modal */}
      {selectedPrint && (
        <div className="print-modal-overlay" onClick={closeModal}>
          <div className="print-modal-content" onClick={e => e.stopPropagation()}>
            <button className="print-modal-close" onClick={closeModal}>&times;</button>
            
            {/* Left Column - Media Gallery */}
            <div className="print-modal-gallery">
              <div className="print-modal-main-image">
                {activeImage.endsWith('.mp4') ? (
                  <video
                    src={activeImage}
                    controls
                    autoPlay
                    loop
                    muted
                    playsInline
                    style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                  />
                ) : (
                  <img src={activeImage} alt={selectedPrint.name} />
                )}
              </div>
              <div className="print-modal-thumbnails">
                {selectedPrint.images.map((image, index) => (
                  <div
                    key={index}
                    className={`print-modal-thumbnail ${activeImage === image ? 'active' : ''}`}
                    onClick={() => handleThumbnailClick(image)}
                  >
                    <img src={image} alt={`View ${index + 1}`} />
                  </div>
                ))}
                {selectedPrint.video && (
                  <div
                    className={`print-modal-thumbnail video ${selectedPrint.video === activeImage ? 'active' : ''}`}
                    onClick={() => handleThumbnailClick(selectedPrint.video!)}
                  >
                    <img src={selectedPrint.mainImage} alt="360Â° view" />
                    <div className="video-indicator">
                      <i className="fas fa-play"></i>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Right Column - Product Info */}
            <div className="print-modal-info">
              <h2 className="print-modal-title">{selectedPrint.name}</h2>
              <p className="print-modal-description">{selectedPrint.description}</p>
              
              <div className="print-modal-details">
                <h4>Product Details</h4>
                <ul className="print-details-list">
                  <li><i className="fas fa-ruler"></i> Dimensions: {selectedPrint.dimensions}</li>
                  <li><i className="fas fa-cube"></i> Material: {selectedPrint.material}</li>
                  <li><i className="fas fa-clock"></i> Production Time: {selectedPrint.productionTime}</li>
                </ul>
              </div>
              
              <div className="print-modal-quantity">
                <label htmlFor="quantity">Quantity:</label>
                <div className="quantity-controls">
                  <button 
                    className="quantity-btn"
                    onClick={() => {
                      if (selectedPrint && selectedPrint.quantity && selectedPrint.quantity > 1) {
                        setSelectedPrint({
                          ...selectedPrint,
                          quantity: selectedPrint.quantity - 1
                        });
                      }
                    }}
                  >
                    -
                  </button>
                  <input
                    type="number"
                    id="quantity"
                    min="1"
                    max="99"
                    value={selectedPrint.quantity || 1}
                    onChange={handleQuantityChange}
                  />
                  <button 
                    className="quantity-btn"
                    onClick={() => {
                      if (selectedPrint && (!selectedPrint.quantity || selectedPrint.quantity < 99)) {
                        setSelectedPrint({
                          ...selectedPrint,
                          quantity: (selectedPrint.quantity || 1) + 1
                        });
                      }
                    }}
                  >
                    +
                  </button>
                </div>
              </div>

              <div className="print-modal-price">{selectedPrint.price}</div>
              
              <button
                className="print-modal-order-btn"
                onClick={() => {
                  if (selectedPrint) {
                    handleAddToCart(selectedPrint);
                    setSelectedPrint(null);
                  }
                }}
              >
                Add to Wishlist
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ThreeDPrints;
