import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import BeforeAfterSlider from './BeforeAfterSlider';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

// Contact information constants
const WHATSAPP_NUMBER = '48512233222';
const MESSENGER_USERNAME = 'aiphotomagic';
const EMAIL_ADDRESS = 'contact@aiphotomagic.com';

const services = [
  {
    id: 'background-magic',
    title: 'Background Magic',
    icon: 'fas fa-magic',
    description: 'Transform your photos with stunning AI-generated backgrounds. Perfect for portraits, product photos, and creative projects.',
    image: '/images/beach-after.jpg',
    features: [
      'Professional quality backgrounds',
      'Multiple style options',
      'Quick turnaround',
      'High-resolution output',
      'Perfect for social media'
    ]
  },
  {
    id: 'fantasy-characters',
    title: 'Fantasy Characters',
    icon: 'fas fa-dragon',
    description: 'Turn yourself into a fantasy character with our AI-powered transformation service. Become a wizard, elf, or any magical being you can imagine.',
    image: '/images/character-after.jpg',
    features: [
      'Multiple fantasy themes',
      'High-quality transformations',
      'Customizable options',
      'Fast delivery',
      'Perfect for cosplay'
    ]
  },
  {
    id: '3d-characters',
    title: 'On-Demand 3D Toy Printing',
    icon: 'fas fa-cube',
    description: "Have a link to a great 3D model of a toy, figure, or character you'd love to hold? We offer professional 3D printing services to bring your chosen models to life!",
    image: '/images/3d-print.jpg',
    features: [
      'High-quality 3D printing (FDM technology)',
      'Printing with safe PLA material in various colors',
      'We print models you provide (link required, subject to review)',
      'Careful finishing and support removal',
      'Get a personalized quote based on the model'
    ],
    directContact: true
  }
];

interface SelectedOption {
  name: string;
  price: string;
  type: 'digital' | 'framed';
}

const ServiceDetailsModal: React.FC<{
  isOpen: boolean;
  onClose: () => void;
  service: typeof services[0] | null;
}> = ({ isOpen, onClose, service }) => {
  const { addToCart, removeFromCart, cartItems } = useCart();
  const [addedItems, setAddedItems] = useState<Set<string>>(new Set());
  const [quantities, setQuantities] = useState<Record<string, number>>({});

  // Sync quantities with cart when modal opens or cart changes
  useEffect(() => {
    if (service) {
      const digitalItemId = `${service.id}-digital`;
      const framedItemId = `${service.id}-framed`;
      
      const digitalItem = cartItems.find(item => item.id === digitalItemId);
      const framedItem = cartItems.find(item => item.id === framedItemId);
      
      setQuantities(prev => ({
        ...prev,
        [digitalItemId]: digitalItem?.quantity || 1,
        [framedItemId]: framedItem?.quantity || 1
      }));
      
      setAddedItems(new Set([
        ...(digitalItem ? [digitalItemId] : []),
        ...(framedItem ? [framedItemId] : [])
      ]));
    }
  }, [service, cartItems]);

  const handleQuantityChange = (variant: 'digital' | 'framed', newQuantity: number) => {
    if (newQuantity < 1) return;
    const itemId = `${service?.id}-${variant}`;
    
    // Update local state
    setQuantities(prev => ({
      ...prev,
      [itemId]: newQuantity
    }));

    // If item is in cart, update cart quantity
    if (addedItems.has(itemId)) {
      const price = variant === 'digital' ? '29' : '99';
      const variantName = variant === 'digital' ? 'Digital Version' : 'Framed Print';
      
      // Remove existing item
      removeFromCart(itemId);
      
      // Add item with new quantity
      addToCart({
        id: itemId,
        name: `${service?.title} - ${variantName}`,
        mainImage: service?.image || '',
        quantity: newQuantity,
        price: `$${price}`
      });
    }
  };

  const handleAddToCart = (variant: 'digital' | 'framed') => {
    if (!service) return;

    const price = variant === 'digital' ? '29' : '99';
    const variantName = variant === 'digital' ? 'Digital Version' : 'Framed Print';
    const itemId = `${service.id}-${variant}`;
    const quantity = quantities[itemId] || 1;

    addToCart({
      id: itemId,
      name: `${service.title} - ${variantName}`,
      mainImage: service.image,
      quantity: quantity,
      price: `$${price}`
    });

    setAddedItems(prev => new Set(prev).add(itemId));
  };

  const handleRemoveFromCart = (variant: 'digital' | 'framed') => {
    if (!service) return;
    const itemId = `${service.id}-${variant}`;
    removeFromCart(itemId);
    setAddedItems(prev => {
      const newSet = new Set(prev);
      newSet.delete(itemId);
      return newSet;
    });
  };

  if (!isOpen || !service) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>
          <i className="fas fa-times"></i>
        </button>
        
        <h3>{service.title}</h3>
        <p className="modal-description">{service.description}</p>
        
        <div className="service-before-after">
          {service.id === 'background-magic' ? (
            <BeforeAfterSlider
              beforeImage="/images/beach-before.jpg"
              afterImage="/images/beach-after.jpg"
              beforeAlt="Original scene"
              afterAlt="Transformed scene"
            />
          ) : service.id === 'fantasy-characters' ? (
            <BeforeAfterSlider
              beforeImage="/images/character-before.jpg"
              afterImage="/images/character-after.jpg"
              beforeAlt="Original character"
              afterAlt="Transformed character"
            />
          ) : (
            <div className="service-image">
              <img src={service.image} alt={service.title} />
            </div>
          )}
        </div>
        
        <div className="service-features">
          <h4>What's Included:</h4>
          <ul>
            {service.features.map((feature, index) => (
              <li key={index}>
                <i className="fas fa-check"></i>
                {feature}
              </li>
            ))}
          </ul>
        </div>

        <div className="service-options">
          {service.id === 'background-magic' ? (
            <>
              <div className="service-option">
                <i className="fas fa-desktop"></i>
                <h4>Digital Version</h4>
                <img 
                  src="/images/background-magic-digital.jpg" 
                  alt="Example of a digital photo with magical background transformation" 
                  className="digital-product-image"
                />
                <p>High-resolution digital file delivered via email</p>
                <div className="price">$29</div>
                <div className="quantity-controls">
                  <span className="quantity-label">Quantity:</span>
                  <button 
                    className="quantity-btn"
                    onClick={() => handleQuantityChange('digital', (quantities[`${service.id}-digital`] || 1) - 1)}
                    disabled={addedItems.has(`${service.id}-digital`)}
                  >
                    -
                  </button>
                  <span className="quantity-value">{quantities[`${service.id}-digital`] || 1}</span>
                  <button 
                    className="quantity-btn"
                    onClick={() => handleQuantityChange('digital', (quantities[`${service.id}-digital`] || 1) + 1)}
                    disabled={addedItems.has(`${service.id}-digital`)}
                  >
                    +
                  </button>
                </div>
                <button 
                  className={`add-to-cart-btn ${addedItems.has(`${service.id}-digital`) ? 'in-cart' : ''}`}
                  onClick={() => addedItems.has(`${service.id}-digital`) ? handleRemoveFromCart('digital') : handleAddToCart('digital')}
                >
                  <i className={`fas ${addedItems.has(`${service.id}-digital`) ? 'fa-check' : 'fa-cart-plus'}`}></i>
                  {addedItems.has(`${service.id}-digital`) ? 'In Cart' : 'Add to Cart'}
                  {addedItems.has(`${service.id}-digital`) && (
                    <span className="remove-from-cart">
                      <i className="fas fa-times"></i>
                    </span>
                  )}
                </button>
              </div>
              <div className="service-option">
                <i className="fas fa-magic"></i>
                <h4>Framed Print</h4>
                <img 
                  src="/images/background-magic-framed.jpg" 
                  alt="Example of a photo with magical background transformation in a frame" 
                  className="framed-product-image"
                />
                <p>Premium quality print with elegant frame</p>
                <div className="price">$49</div>
                <div className="quantity-controls">
                  <span className="quantity-label">Quantity:</span>
                  <button 
                    className="quantity-btn"
                    onClick={() => handleQuantityChange('framed', (quantities[`${service.id}-framed`] || 1) - 1)}
                    disabled={addedItems.has(`${service.id}-framed`)}
                  >
                    -
                  </button>
                  <span className="quantity-value">{quantities[`${service.id}-framed`] || 1}</span>
                  <button 
                    className="quantity-btn"
                    onClick={() => handleQuantityChange('framed', (quantities[`${service.id}-framed`] || 1) + 1)}
                    disabled={addedItems.has(`${service.id}-framed`)}
                  >
                    +
                  </button>
                </div>
                <button 
                  className={`add-to-cart-btn ${addedItems.has(`${service.id}-framed`) ? 'in-cart' : ''}`}
                  onClick={() => addedItems.has(`${service.id}-framed`) ? handleRemoveFromCart('framed') : handleAddToCart('framed')}
                >
                  <i className={`fas ${addedItems.has(`${service.id}-framed`) ? 'fa-check' : 'fa-cart-plus'}`}></i>
                  {addedItems.has(`${service.id}-framed`) ? 'In Cart' : 'Add to Cart'}
                  {addedItems.has(`${service.id}-framed`) && (
                    <span className="remove-from-cart">
