import React, { createContext, useContext, useState, useEffect } from 'react';

interface CartItem {
  id: string;
  name: string;
  mainImage?: string;
  quantity?: number;
  price?: string;
}

interface CartContextType {
  cartItems: CartItem[];
  addToCart: (item: CartItem) => void;
  removeFromCart: (itemId: string) => void;
  cartCount: number;
  isCartOpen: boolean;
  openCart: () => void;
  closeCart: () => void;
  getTotalPrice: () => string;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

export const CartProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  const [isCartOpen, setIsCartOpen] = useState(false);

  useEffect(() => {
    // Load cart items from localStorage on mount
    const storedCart = localStorage.getItem('tdPrintsCart');
    if (storedCart) {
      setCartItems(JSON.parse(storedCart));
    }
  }, []);

  const addToCart = (item: CartItem) => {
    setCartItems(prevItems => {
      // Check if item already exists in cart
      if (prevItems.some(cartItem => cartItem.id === item.id)) {
        return prevItems;
      }
      
      const newItems = [...prevItems, item];
      localStorage.setItem('tdPrintsCart', JSON.stringify(newItems));
      return newItems;
    });
  };

  const removeFromCart = (itemId: string) => {
    setCartItems(prevItems => {
      const newItems = prevItems.filter(item => item.id !== itemId);
      localStorage.setItem('tdPrintsCart', JSON.stringify(newItems));
      return newItems;
    });
  };

  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => {
      if (!item.price) return total;
      
      // Extract numeric value from price string (e.g., "$29.99" -> 29.99)
      const priceValue = parseFloat(item.price.replace(/[^0-9.-]+/g, ''));
      const quantity = item.quantity || 1;
      
      return total + (priceValue * quantity);
    }, 0).toFixed(2);
  };

  const openCart = () => setIsCartOpen(true);
  const closeCart = () => setIsCartOpen(false);

  const value = {
    cartItems,
    addToCart,
    removeFromCart,
    cartCount: cartItems.length,
    isCartOpen,
    openCart,
    closeCart,
    getTotalPrice
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (context === undefined) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
