import React from 'react';
import { motion } from 'framer-motion';

const steps = [
  {
    number: '1',
    icon: 'ðŸ“¸',
    title: 'Send Your Photos',
    description: 'The easiest way is to send a few photos via Messenger or WhatsApp at +48 512 233 222. The photos can be taken with your phone.',
    buttons: [
      {
        text: 'Chat on WhatsApp',
        icon: 'ðŸ“±',
        link: 'https://wa.me/48512233222',
        style: 'whatsapp'
      },
      {
        text: 'Message Us',
        icon: 'ðŸ’¬',
        link: 'https://m.me/aiphotomagic',
        style: 'messenger'
      }
    ]
  },
  {
    number: '2',
    icon: 'âœ¨',
    title: 'Free Design Preview',
    description: 'Within a few hours, we will send you a FREE design of the painting. WOW effect guaranteed ðŸ˜Š',
    subtext: 'If you don\'t like it, you can cancel without any consequences, and we will delete the photos.'
  },
  {
    number: '3',
    icon: 'ðŸ“¦',
    title: 'Order & Delivery',
    description: 'If you do like the design, only then do you place an order for the painting.',
    subtext: 'You can choose to pay via bank transfer or cash on delivery, and the finished painting will be shipped by courier to your chosen address the very next day.'
  }
];

function HowItWorks() {
  return (
    <div className="how-it-works-section">
      <div className="how-it-works-container">
        <motion.div
          className="how-it-works-header"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h2>How to Order</h2>
          <p>Simple steps to transform your photos into magic</p>
        </motion.div>

        <div className="steps-container">
          {steps.map((step, index) => (
            <motion.div
              key={step.number}
              className="step-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
            >
              <div className="step-number">{step.number}</div>
              <div className="step-icon">{step.icon}</div>
              <h3>{step.title}</h3>
              <p className="step-description">{step.description}</p>
              
              {step.buttons && (
                <div className="step-buttons">
                  {step.buttons.map((button) => (
                    <a
                      key={button.text}
                      href={button.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className={`step-button ${button.style}`}
                    >
                      <span className="button-icon">{button.icon}</span>
                      {button.text}
                    </a>
                  ))}
                </div>
              )}
              
              {step.subtext && (
                <p className="step-subtext">{step.subtext}</p>
              )}
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default HowItWorks;
